<?php	if (!empty($_SERVER['SCRIPT_FILENAME']) && 'comments.php' == basename($_SERVER['SCRIPT_FILENAME']))			die (__('Please do not load this page directly. Thanks!', 'FoodRecipe'));	if ( post_password_required() ) { ?>			<p class="nocomments"><?php _e('This post is password protected. Enter the password to view comments.', 'FoodRecipe'); ?></p>	<?php			return;	}?><!-- You can start editing here. --><?php if ( have_comments() ) : ?>		<h2 id="comments"><?php comments_number(__('Comment <span>(0)</span>', 'FoodRecipe'), __('Comment <span>(1)</span>', 'FoodRecipe'), __('Comments <span>(%)</span>', 'FoodRecipe') ); ?></h2>	    <span class="w-pet-border"></span>		<ol class="comment-list">		<?php wp_list_comments('type=comment&callback=recipe_comment'); ?>		</ol><?php else : // this is displayed if there are no comments so far ?>		<?php if ( comments_open() ) : ?>			<!-- If comments are open, but there are no comments. -->		 <?php else : // comments are closed ?>			<!-- If comments are closed. --><!--				<p class="nocomments">--><?php //_e('Comments are closed.', 'FoodRecipe'); ?><!--</p>-->		<?php endif; ?><?php endif; ?><?php /* BEGIN TRACKBACK/PINGBACK CODE */ ?><?php global $trackbacks; ?><?php if ($trackbacks) : ?>		<?php $comments = $trackbacks; ?>        <div id="pingback-trackback">				<h3 id="trackbacks"><?php echo sizeof($trackbacks); _e('Trackbacks/Pingbacks', 'FoodRecipe'); ?></h3>                <ol class="pings">				<?php foreach ($comments as $comment) : ?>			<!-- Start Your trackback Code -->					<li <?php echo $oddcomment; ?>id="comment-<?php comment_ID() ?>">						<cite><?php comment_author_link() ?></cite>						<?php if ($comment->comment_approved == '0') : ?>						<em><?php _e('Your comment is awaiting moderation.', 'FoodRecipe'); ?></em>						<?php endif; ?>			 		</li>			<!-- End Your trackback Code -->				<?php					/* Changes every other comment to a different class */					$oddcomment = ( empty( $oddcomment ) ) ? 'class="alt" ' : '';				?>				<?php endforeach; /* end for each comment */ ?>				</ol>		</div><?php endif; ?><?php /* END TRACKBACK/PINGBACK CODE */ ?><?php    if ( comments_open() ) :        $cform_args = array(        );        $cform_args = apply_filters('framework_comment_form_args', $cform_args);        comment_form($cform_args);    endif; // if you delete this the sky will fall on your head?>