<?php/*-----------------------------------------------------------------------------------*//*	Add Twitter, Facebook and Google URL links to User Profile./*-----------------------------------------------------------------------------------*/if(!function_exists('my_user_contactmethods')){    add_filter('user_contactmethods', 'my_user_contactmethods');    function my_user_contactmethods($user_contactmethods)    {        $user_contactmethods['user_designation'] = __('User Designation','FoodRecipe');        $user_contactmethods['twitter'] = __('Twitter URL','FoodRecipe');        $user_contactmethods['facebook'] = __('Facebook URL','FoodRecipe');        $user_contactmethods['google'] = __('Google URL','FoodRecipe');        $user_contactmethods['linkedin'] = __('LinkedIn URL','FoodRecipe');        $user_contactmethods['youtube'] = __('YouTube URL','FoodRecipe');        $user_contactmethods['photo'] = __('Photo URL','FoodRecipe');        return $user_contactmethods;    }}?>