<?phpif( !function_exists( 'recipe_comment' ) ){    function recipe_comment($comment, $args, $depth) {        $GLOBALS['comment'] = $comment;        ?>    <li <?php comment_class(); ?>  id="comment-<?php comment_ID() ?>">        <div class="img-box">            <a href="<?php echo get_comment_author_url(); ?>">                <?php echo get_avatar( $comment->comment_author_email, 67 ); ?>            </a>        </div>        <div class="comment-body">            <p class="meta"><?php _e('posted by ', 'FoodRecipe'); ?> <?php comment_author_link(); ?> <?php _e(' on', 'FoodRecipe'); ?> <span> <?php echo get_comment_date(); ?> </span></p>            <div class="comment"><?php comment_text(); ?></div>            <?php if ($comment->comment_approved == '0') : ?>                <em><?php _e('Your comment is awaiting moderation.','FoodRecipe') ?></em>                <br />            <?php endif; ?>            <div class="reply">                <?php edit_comment_link(__('(Edit)', 'FoodRecipe'),'  ','') ?> &nbsp; <?php comment_reply_link(array_merge( $args, array('depth' => $depth, 'max_depth' => $args['max_depth']))) ?>            </div>        </div>    <?php    }}?>