<?phpif(!function_exists('theme_pagination')){    function theme_pagination($pages = ''){        global $paged;        $paged = get_query_var( 'paged' );        if(empty($paged))$paged = 1;        $prev = $paged - 1;        $next = $paged + 1;        $range = 3; // only change it to show more links        $showitems = ($range * 2)+1;        if($pages == '')        {            global $wp_query;            $pages = $wp_query->max_num_pages;            if(!$pages)            {                $pages = 1;            }        }        if(1 != $pages){            echo "<div id='pagination'>";            echo ($paged > 2 && $paged > $range+1 && $showitems < $pages)? "<a href='".get_pagenum_link(1)."' class='btn'>&laquo; ".__('First', 'FoodRecipe')."</a> ":"";            echo ($paged > 1 && $showitems < $pages)? "<a href='".get_pagenum_link($prev)."' class='btn'>&laquo; ". __('Previous', 'FoodRecipe')."</a> ":"";            for ($i=1; $i <= $pages; $i++){                if (1 != $pages &&( !($i >= $paged+$range+1 || $i <= $paged-$range-1) || $pages <= $showitems )){                    echo ($paged == $i)? "<a href='".get_pagenum_link($i)."' class='btn current'>".$i."</a> ":"<a href='".get_pagenum_link($i)."' class='btn'>".$i."</a> ";                }            }            echo ($paged < $pages && $showitems < $pages) ? "<a href='".get_pagenum_link($next)."' class='btn'>". __('Next', 'FoodRecipe') ." &raquo;</a> " :"";            echo ($paged < $pages-1 &&  $paged+$range-1 < $pages && $showitems < $pages) ? "<a href='".get_pagenum_link($pages)."' class='btn'>". __('Last', 'FoodRecipe') ." &raquo;</a> ":"";            echo "</div>";        }    }}?>