<!-- Recipe Information --><ul class="recipe-info clearfix">    <?php    $yield = rwmb_meta( 'RECIPE_META_yield' );    $servings = rwmb_meta( 'RECIPE_META_servings' );    $prep_time = convert_to_hours(rwmb_meta( 'RECIPE_META_prep_time' ));    $cook_time = convert_to_hours(rwmb_meta( 'RECIPE_META_cook_time' ));    $ready_in = convert_to_hours(rwmb_meta( 'RECIPE_META_ready_in' ));    if(!empty($yield))    {        ?>        <li class="yield">            <strong class="name"><?php _e('Yield :','FoodRecipe'); ?></strong>            <span class="value" itemprop="recipeYield"><?php echo $yield; ?></span>        </li>    <?php    }    if(!empty($servings))    {        ?>        <li class="servings">            <strong class="name"><?php _e('Servings :','FoodRecipe'); ?></strong>            <span class="value"><?php echo $servings; ?></span>        </li>    <?php    }    if(!empty($prep_time))    {        ?>        <li class="prep-time">            <strong class="name"><?php _e('Prep Time :','FoodRecipe'); ?></strong>            <span class="value"><meta itemprop="prepTime" content="PT<?php echo rwmb_meta('RECIPE_META_prep_time'); ?>M"><?php echo $prep_time; ?></span>        </li>    <?php    }    if(!empty($cook_time))    {        ?>        <li class="cook-time">            <strong class="name"><?php _e('Cook Time :','FoodRecipe'); ?></strong>            <span class="value"><meta itemprop="cookTime" content="PT<?php rwmb_meta('RECIPE_META_cook_time'); ?>"><?php echo $cook_time; ?></span>        </li>    <?php    }    if(!empty($ready_in))    {        ?>        <li class="ready-in">            <strong class="name"><?php _e('Ready In :','FoodRecipe'); ?></strong>            <span class="value"><?php echo $ready_in; ?></span>        </li>    <?php    }    ?></ul>