<?php/*-----------------------------------------------------------------------------------*//*	For Loading Required JS and Files/*-----------------------------------------------------------------------------------*/if(!function_exists('load_theme_scripts')){    function load_theme_scripts()    {        if (!is_admin()) {            $jscriptURL = get_template_directory_uri().'/js/';            // Enquer Scripts            wp_enqueue_script('jquery');            wp_enqueue_script('validate', $jscriptURL.'jquery.validate.min.js',array('jquery'), '1.11.1', false);            wp_enqueue_script('forms', $jscriptURL.'jquery.form.min.js',array('jquery'), '3.48.0', false);            wp_enqueue_script('jsEasing', ($jscriptURL.'jquery.easing.1.3.js'), array('jquery'), '1.3', false);            wp_enqueue_script('prettyphoto', $jscriptURL.'prettyPhoto/js/jquery.prettyPhoto.js', array('jquery'), '3.1.3', false);            wp_enqueue_script('jQueryUI', $jscriptURL.'jquery-ui-1.9.2.custom.js', array('jquery'), '1.9.2', false);            wp_enqueue_script('cyclePlugintwo', $jscriptURL.'jquery.cycle2.js', array('jquery'), '2.0130909', false);            wp_enqueue_script('nivoSlider', $jscriptURL.'nivo-slider/jquery.nivo.slider.js', array('jquery'), '2.7.1', false);            wp_enqueue_script('accordionSlider', $jscriptURL.'accordion-slider.js', array('jquery'), '1.0', false);            wp_enqueue_script('custom_script',$jscriptURL.'script.js', array('jquery'), '1.0', true);            if ( is_singular() ){                wp_enqueue_script( 'comment-reply' );            }        }    }}add_action('wp_enqueue_scripts', 'load_theme_scripts');?>