<?php/*-----------------------------------------------------------------------------------*//*	For Loading Required CSS Files/*-----------------------------------------------------------------------------------*/if(!function_exists('load_my_styles')){    function load_my_styles()    {        if (!is_admin())        {            wp_register_style('nivoslider',  get_template_directory_uri() . '/js/nivo-slider/nivo-slider.css');            wp_register_style('prettyPhoto',  get_template_directory_uri() . '/js/prettyPhoto/css/prettyPhoto.css');            wp_register_style('blueskin',  get_template_directory_uri() . '/skin/blue/blue.css');            wp_register_style('redskin',  get_template_directory_uri() . '/skin/red/red.css');            wp_register_style('orangeskin',  get_template_directory_uri() . '/skin/orange/orange.css');            wp_register_style('responsive',  get_template_directory_uri() . '/responsive.css');            // Enquer Styles            wp_enqueue_style('nivoslider');            wp_enqueue_style('prettyPhoto');            wp_enqueue_style('responsive');            /* Theme Skins */            if ( function_exists( 'ot_get_option' ) ) {                $theme_skin = ot_get_option('skin_set');            }            if(isset($_GET['skin'])){                $theme_skin = $_GET['skin'];            }elseif(isset($_COOKIE['skin'])){                $theme_skin = $_COOKIE['skin'];            }            if(!empty($theme_skin)){                switch($theme_skin){                    case 'skin_blue':                        wp_enqueue_style('blueskin');                        break;                    case 'skin_red':                        wp_enqueue_style('redskin');                        break;                    case 'skin_orange':                        wp_enqueue_style('orangeskin');                        break;                }            }            wp_enqueue_style('responsive');        }    }}add_action('wp_enqueue_scripts', 'load_my_styles');?>