<?php global $post;$nut_names = rwmb_meta( 'RECIPE_META_nut_name' );$nut_vals = rwmb_meta( 'RECIPE_META_nut_mass' );$nut_number = 0;if(is_array($nut_names)){	$nut_number = count($nut_names);}if( $nut_number >= 1 && (!empty($nut_names[0])) ){	$i = 0;	?>	<div class="nutritional" itemprop="nutrition" itemscope itemtype="http://schema.org/NutritionInformation">			<h3><?php _e('Nutritional Info', 'FoodRecipe'); ?></h3>			<p><?php _e('This information is per serving.', 'FoodRecipe'); ?></p>			<ul>			<?php            foreach(array_combine(array_values($nut_names), array_values($nut_vals)) as $nutname => $nutvalue){					?>						<li class="nutrition">								<p class="type"><?php echo $nutname; ?></p>								<span class="value"><?php echo $nutvalue; ?></span>						</li>					<?php					$i++;				}			?>			</ul>	</div>	<?php}?>