<div <?php post_class('recipe-listing-item clearfix'); ?> id="post-<?php the_ID(); ?>">    <?php    if(has_post_thumbnail($post->ID))    {        $lightbox_check = ( function_exists( 'ot_get_option' ) ) ? ot_get_option('pp_ctrl_for_recipe') : false;        $image_id = get_post_thumbnail_id();        if($image_id) { $image_url = wp_get_attachment_image_src($image_id,'full-size', true); }        ?>        <div class="post-thumb single-img-box">            <a <?php echo ($lightbox_check == 'true') ? 'rel="prettyPhoto" href="'.$image_url[0].'"' : 'href="'.get_permalink().'"'; ?> title="<?php the_title(); ?>">                <?php the_post_thumbnail('recipe-listing'); ?>            </a>        </div>    <?php    }    ?>    <div class="recipe-info">        <h2><a href="<?php the_permalink(); ?>"><?php the_title(); ?></a></h2>        <div class="recipe-tags">            <span class="type"><?php echo get_the_term_list( $post->ID, 'recipe_type', __(' Recipe Type: ', 'FoodRecipe'), ', ', ''); ?></span>            <span class="cuisine"><?php echo get_the_term_list( $post->ID, 'cuisine', __(' Cuisine: ', 'FoodRecipe'), ', ', ''); ?></span>        </div>        <?php            $rating_check = ( function_exists( 'ot_get_option' ) ) ? ot_get_option('recipe_show_rating') : false;            if($rating_check == 'show_rating'){        ?>            <div class="rating">                <?php the_recipe_rating($post->ID); ?>                <span><?php _e('Average Rating:', 'FoodRecipe'); ?> <span>(<?php echo get_avg_rating($post->ID); ?> / 5)</span></span>            </div>        <?php            }else { echo '<hr>'; }        ?>        <p><?php echo word_trim(get_the_excerpt(),15,' ...'); ?></p>        <a href="<?php the_permalink(); ?>" class="readmore"><?php _e('Read more', 'FoodRecipe'); ?></a>    </div></div><!-- end of post div -->