<div id="left-area" class="clearfix full-wide hrecipe" itemscope itemtype="http://schema.org/Recipe">    <!-- Starting Default Loop -->    <?php if (have_posts()) while (have_posts()) : the_post(); ?>        <h1 class="title fn" itemprop="name" ><?php the_title(); ?></h1>        <!-- for Schema.org microdata -->        <meta itemprop="url" content="<?php the_permalink(); ?>">        <meta itemprop="datePublished" content="<?php the_time('Y-m-d'); ?>" />        <span class="published"><?php the_time('Y-m-d'); ?></span>        <!-- Recipe Categorization Information -->        <ul class="recipe-cat-info clearfix">            <li>                <span itemscope="recipeCuisine"><?php echo get_the_term_list( $post->ID, 'cuisine', __('Cuisine: ', 'FoodRecipe'), ', ', ''); ?></span>            </li>            <li>                <?php echo get_the_term_list( $post->ID, 'course', __(' Course: ', 'FoodRecipe'), ', ', ''); ?>            </li>            <li itemscope="typicalAgeRange">                <?php echo get_the_term_list( $post->ID, 'skill_level', __('Skill Level: ', 'FoodRecipe'), ', ', ''); ?>            </li>        </ul>        <?php        $recipe_images = rwmb_meta( 'RECIPE_META_more_images_recipe', 'type=plupload_image&size=single-carousel-thumb', $post->ID );        $recipe_images_size2 = rwmb_meta( 'RECIPE_META_more_images_recipe', 'type=plupload_image&size=thumbnail-blog', $post->ID );        $images_count = count($recipe_images);        if($images_count > 0)        {            ?>            <div class="single-imgs">                <div class="single-img-box">                    <div class="recipe-single-img">                        <?php                        foreach($recipe_images as $my_image_id=>$sfeimage){ $srImage = $sfeimage; break; }                        $firstimgpath = wp_get_attachment_image_src( $my_image_id, 'full-size', false );                        $pp_lb_check = ( function_exists( 'ot_get_option' ) ) ? ot_get_option('pp_ctrl_for_recipe') : false;                        ?>                        <a <?php echo ($pp_lb_check == 'true') ? 'data-rel="prettyphoto" href="'.$firstimgpath[0].'"' : 'data-rel="none"'; ?> title="<?php the_title(); ?>">                            <?php echo wp_get_attachment_image($my_image_id, 'thumbnail-blog', false, array( 'class' => 'photo', 'alt' => '', 'itemprop' => 'image' )); ?>                       </a>                    </div>                </div>                <div id="horiz_container_outer" class="small-img-cont">                    <ul id="horiz_container" class="small-imgs">                        <?php                        foreach($recipe_images as $my_image_id=>$image)                        {                            ?>                            <li>                                <?php                                $image_fullsize = $image['full_url'];                                $rcaption = $image['caption'];                                ?>                                <a data-rel="<?php echo $recipe_images_size2[$my_image_id]['url']; ?>" data-ppurl="<?php echo $image_fullsize; ?>" data-ppcaption="<?php echo $rcaption; ?>">                                    <?php echo '<img src="'.$image['url'].'" alt="'.$rcaption.'">'; ?>                                </a>                            </li>                        <?php                        }                        ?>                    </ul>                    <div class="carnav">                        <span class="left"></span>                        <span class="right"></span>                    </div>                </div>            </div>        <?php        }        elseif(has_post_thumbnail())        {            $image_id = get_post_thumbnail_id();            $image_url = wp_get_attachment_image_src($image_id,'full-size', true);            ?>            <div class="single-imgs">                <div class="single-img-box">                    <?php                    if( function_exists( 'ot_get_option' ) ){                    ?>                        <a  <?php if(ot_get_option('pp_ctrl_for_recipe') == 'show_lightbox'){ echo 'rel="prettyPhoto" href="'.$image_url[0].'"'; } ?> title="<?php the_title(); ?>">                            <?php the_post_thumbnail('thumbnail-blog', array( 'class' => 'photo', 'itemprop' => 'image' )); ?>                        </a>                    <?php                    }                    ?>                </div>                <div id="horiz_container_outer" class="small-img-cont">                    <p class="info-msg msg-error"><span><?php _e('No more images found for this recipe!', 'FoodRecipe'); ?></span></p>                </div>            </div>        <?php        }        ?>        <?php get_template_part('functions/recipe-info'); ?>        <span class="w-pet-border"></span>        <div class="info-left instructions" itemprop="about">            <span itemprop="description"><?php the_content(); ?></span>            <div class="recipe-tags clearfix">                <span class="type" itemprop="recipeCategory"><?php echo get_the_term_list( $post->ID, 'recipe_type', __(' Recipe Type: ', 'FoodRecipe'), ', ', ''); ?></span>                <span class="tags"><?php the_tags(__('Tags: ', 'FoodRecipe'),', ',''); ?> </span>                <span class="ingredient"><?php echo get_the_term_list( $post->ID, 'ingredient', __(' Ingredients: ', 'FoodRecipe'), ', ', ''); ?></span>                <!-- Share Icons -->                <?php                $social_check = ( function_exists( 'ot_get_option' ) ) ? ot_get_option('recipe_show_social_icons') : false;                if($social_check == 'show_social')                    get_template_part('inc/share');                ?>                <meta itemprop="interactionCount" content="UserComments:<?php echo get_comments_number(); ?>" />            </div>            <span class="w-pet-border"></span>            <?php            if (!get_comments_number()==0) {                ?>                <h3 class="blue"><?php _e('Recipe Comments ', 'FoodRecipe');?></h3>            <?php            }            ?>            <div class="comments">                <?php comments_template('',true); ?>            </div><!-- end of comments div -->        </div><!-- end of info-left div -->        <div class="info-right">            <!-- Cook Info -->            <?php get_template_part('inc/cook-info'); ?>            <!-- Rating Icons -->            <?php            $rating_check = ( function_exists( 'ot_get_option' ) ) ? ot_get_option('recipe_show_rating') : false;            if($rating_check == 'show_rating')                get_template_part('inc/rating');            ?>            <!-- Including More Recipres part -->            <?php get_template_part('inc/more-recipes-loop'); ?>            <!-- Including Nutritional Info part -->            <?php get_template_part('inc/nutritional-info'); ?>            <?php if ( !dynamic_sidebar( 'Recipe Right Advertisement Area' )) : endif; ?>        </div><!-- end of info-right div -->        <span class="w-pet-border"></span>    <?php endwhile; ?></div><!-- end of left-area -->