<?php get_header(); ?>						<div id="left-area" class="clearfix">                        		<?php if (have_posts()) : while (have_posts()) : the_post(); ?>								<div <?php post_class(); ?> id="post-<?php the_ID(); ?>">										<h1 class="single-post-title"><?php the_title(); ?></h1>										<p class="meta"><?php _e('By', 'FoodRecipe'); ?> : <?php the_author_posts_link() ?> <span>|</span> <span class="comments"><?php comments_popup_link(__('0 Comments', 'FoodRecipe'), __('1 Comment', 'FoodRecipe'), __('% Comments', 'FoodRecipe')); ?></span> <span>|</span> On : <?php the_time('F j, Y'); ?> <span>|</span> <?php _e('Category', 'FoodRecipe'); ?> : <span class="cats"><?php the_category(', '); ?></span></p>                                        <div class="tags">                                		    <?php the_tags(); ?>                                        </div><!-- end of tags div -->										<?php											if(has_post_thumbnail())											{                                                $image_id = get_post_thumbnail_id();                                                $image_url = wp_get_attachment_image_src($image_id,'full-size', true);												?>                                                <?php if ( function_exists( 'ot_get_option' ) ) { ?>                                                    <div class="post-thumb single-img-box">                                                            <a <?php if(ot_get_option('pp_ctrl_for_blog') == 'show_lightbox'){ echo 'rel="prettyPhoto" href="'.$image_url[0].'"'; } ?> title="<?php the_title(); ?>">                                                                    <?php the_post_thumbnail('thumbnail-blog'); ?>                                                            </a>                                                    </div>		                                        <?php                                                }											}										the_content();                                        if ( function_exists( 'ot_get_option' ) ) {                                            // Share Icons -->                                            $social_check = ot_get_option('post_show_social_icons');                                            if($social_check == 'show_social'){                                                echo '<div class="blog-post-social">                                                        <h5>Share This Post!</h5>';                                                get_template_part('inc/share');                                                echo '</div>';                                            }                                        }                                        ?>								</div><!-- end of post div -->								<?php wp_link_pages(array('before' => '<div class="pages-navigation"><strong>'.__('Pages &nbsp;', 'FoodRecipe').'</strong> ', 'after' => '</div>', 'link_before' => '<span>',	'link_after' => '</span>', 'next_or_number' => 'number')); ?>								<?php endwhile; ?>                            	<?php else : ?>                                <div <?php post_class(); ?> id="post-<?php the_ID(); ?>">										<h2 class="post-title"><?php _e('No Post Found', 'FoodRecipe') ?></h2>								</div><!-- end of post div -->								<?php endif; ?>                                <div class="comments">                                		<?php comments_template('',true); ?>                                </div><!-- end of comments div -->						</div><!-- end of left-area -->				        <!-- LEFT AREA ENDS HERE -->	<?php get_sidebar(); ?><?php get_footer(); ?>