						<!-- SLIDER STARTS HERE -->						<?php                        if ( function_exists( 'ot_get_option' ) )                        {                        $slides = ot_get_option( 'accor_slider_images', array()  );                            if ( ! empty( $slides ) ) {                        ?>                        <div id="slider" class="accordionSlider">                            <div id="slider_frame" class="accor-slider">                                <ul id="accordion-slider" class="sm">                                        <?php                                            $slides = ot_get_option( 'accor_slider_images', array()  );                                                    foreach( $slides as $slide ) {                                                        echo '<li><a href="'.$slide['link'].'"><img src="'.$slide['image'].'" alt="'.$slide['title'].'" /></a></li>';                                                    }                                        ?>                                </ul>                            </div>						</div><!-- end of Slider div -->                        <?php                            } else {                                ?>                            <div id="slider" class="no-slides-error">                                <?php _e('Please add slides to your selected slider in theme options. Follow documentation for more detail.', 'FoodRecipe'); ?>                            </div>                                <?php                            }                        }                        ?>						<!-- SLIDER AREA ENDS HERE -->