						<!-- SLIDER STARTS HERE -->                        <?php                        if ( function_exists( 'ot_get_option' ) )                        {                        $slides = ot_get_option( 'basic_image_slider', array() );                        if ( ! empty( $slides ) ) {                        ?>						<div id="slider">								<h2 class="slider-head"></h2>								<p class="slogan">									<?php										echo ot_get_option( 'b_slider_statement' );									?>								</p>								<div class="slides basic ">										<ul class="cycle-slideshow"  data-cycle-fx=scrollHorz data-cycle-timeout=4000 data-cycle-slides="li" data-cycle-pager=".cycle-pager">												<?php															foreach( $slides as $slide )															{																	$bs_slider_args = array( 'post_type'=> array('post','recipe') , 'posts_per_page' => 1, 'post__in' => array($slide['id']) );																	$bs_slider_query = new WP_Query( $bs_slider_args );																	if ( $bs_slider_query->have_posts() )																	while ( $bs_slider_query->have_posts() ) :																		$bs_slider_query->the_post();																			?>																			<li>																					<img src="<?php echo $slide['image']; ?>" alt="<?php the_title(); ?>" />																					<div class="about">																							<h2><a href="<?php the_permalink(); ?>"><?php the_title(); ?></a></h2>																							<p><?php echo word_trim(get_the_excerpt(),28,'...'); ?></p>																							<a href="<?php the_permalink(); ?>" class="readmore"><?php _e('Read more', 'FoodRecipe'); ?></a>																					</div>																			</li>																			<?php																	endwhile;															}											?>										</ul>                                    <div class="sliderNav">                                        <div class="cycle-pager"></div>                                    </div>								</div><!-- end of slides -->						</div><!-- end of Slider div -->                        <?php                            } else {                            ?>                            <div id="slider" class="no-slides-error">                                <?php _e('Please add slides to your selected slider in theme options. Follow documentation for more detail.', 'FoodRecipe'); ?>                            </div>                        <?php                            }                        }                        ?>                        <!-- SLIDER AREA ENDS HERE -->