						<!-- SLIDER STARTS HERE -->                        <?php                        if ( function_exists( 'ot_get_option' ) )                        {                            $slides = ot_get_option( 'thumb_slider_images', array() );                            if ( ! empty( $slides ) ) {                                ?>                        <div id="slider">								<div class="thumb-slider-wrap">                                    <div class="cycle-slideshow thumb-slider"                                         data-cycle-fx=scrollHorz                                         data-cycle-pager="#adv-custom-pager"                                         data-cycle-pager-template="<a href='#' ><img src='{{src}}' width=130 height=53></a>">												<?php                                                        foreach( $slides as $slide ) {                                                            echo '<img src="'.$slide['image'].'" alt="'.$slide['title'].'" />';                                                        }							                    ?>										</div>								</div>								<?php	                                if ( ! empty( $slides ) ) {										?>                                        <div id=adv-custom-pager class="sliderThumbs center external"></div>										<?php	                                 }                                 ?>						</div><!-- end of Slider div -->                        <?php                        } else {                            ?>                            <div id="slider" class="no-slides-error">                                <?php _e('Please add slides to your selected slider in theme options. Follow documentation for more detail.', 'FoodRecipe'); ?>                            </div>                        <?php                        }                        }                        ?>            			<!-- SLIDER AREA ENDS HERE -->