<?php /** Template Name: Recipes Listing Template */ get_header(); ?>						<div id="left-area" class="clearfix">								<h1><?php the_title(); ?></span></h1>                                <span class="w-pet-border"></span><br />                        		<?php                                     if ( function_exists( 'ot_get_option' ) ) {                                         $recipes_per_page = ot_get_option('recipes_per_page_listing');                                         if(empty($recipes_per_page)) { $recipes_per_page = 6; }                                     }									$recipe_args = array( 'post_type'=>'recipe', 'posts_per_page' => $recipes_per_page, 'paged' => $paged );									$recipe_query = new WP_Query( $recipe_args );									if ( $recipe_query->have_posts() ) :											while ( $recipe_query->have_posts() ) :											$recipe_query->the_post();                                                get_template_part( 'inc/recipe-post' );                                            endwhile; ?>                            	<?php else : ?>                                <div <?php post_class(); ?> id="post-<?php the_ID(); ?>">										<h2 class="post-title"><?php _e('No Recipes Found', 'FoodRecipe') ?></h2>								</div><!-- end of post div -->								<?php endif; ?>										<?php											// Pagination											theme_pagination( $recipe_query->max_num_pages);										?>						</div><!-- end of left-area -->				        <!-- LEFT AREA ENDS HERE -->                        <div id="sidebar">								<?php if ( ! dynamic_sidebar( 'Recipe Sidebar' )) : endif; ?>                        </div><?php get_footer(); ?>